import React, { useState, useEffect }  from "react";
import { Container, Row } from "react-bootstrap";
import apiClient from "../apiClient";
import NavHeader from "../header/NavHeader";
import ListElemRelevant from "./ListElemRelevant";

const Relevant = () => {

        const [bookings, setBookings] = useState([]);
        const [bookingArray, setBookingArray] = useState([]);

        const makeRow = (array, size) => {
            const result = [];
            for (let i = 0; i < array.length; i += size) {
                result.push(array.slice(i, i + size));
            }
            return result
        }

    useEffect(() => {
        const fetchGet = async () => {
            const response = await apiClient.get("api/v1/booking/relevant");
            setBookings(response.data)
        }

        fetchGet();
    }, []);    

    useEffect(() => {
        if (bookings.length > 0) {
            setBookingArray(bookings);
        }
    }, [bookings]);

    console.log(bookingArray);
    return (
        <>
        <NavHeader/>
        <Container className="xl mt-3"><h1>Текущие записи</h1></Container>
        <Container className="mt-4 d-flex flex-wrap gap-2">
            {bookingArray.map(
                (elem, index) =><ListElemRelevant className = "col-2" key = {index} booking = {elem}/>)}
            
        </Container>
        </>
    )
}

export default Relevant;
