import React, { useState, useEffect }  from "react";
import { Container, Pagination, Row } from "react-bootstrap";
import apiClient from "../apiClient";
import NavHeader from "../header/NavHeader";
import ServiceElem from "./ServiceElem";
import DoctorsElem from "./DoctorsElem";
import DateTimeElem from "./DateTimeElem";


const PickDateTime = (props) => {

        const [dates, setDates] = useState([]);
        const [datesArray, setDatesArray] = useState([]);
        const [pickDate, setPickDate] = useState(null);

        let allDates = ["9:00", "10:00", "11:00", "12:00", "13:00", "14:00", "15:00", "16:00", "17:00", "17:30" , "18:00", "19:00"];
        allDates = allDates.map(elem => {
                const newDate = new Date();
                const hm = elem.split(":")
                newDate.setHours(+hm[0]);
                newDate.setMinutes(+hm[1]);
                newDate.setSeconds(0);
                return newDate;
            });

        const makeRow = (array, size) => {
            const result = [];
            for (let i = 0; i < array.length; i += size) {
                result.push(array.slice(i, i + size));
            }
            return result
        }

    useEffect(() => {
        const fetchGet = async () => {
            const response = await apiClient.get(`/api/v1/doctor/bookings?doctorId=${sessionStorage.getItem("doctorId")}`);
            setDates(response.data.map(elem => new Date(elem)));
        }

        fetchGet();
    }, []);    

    useEffect(() => {
        if (dates.length > 0) {
            setDatesArray(makeRow(allDates, 4));
        }
    }, [dates]);

    const setDateForAll = (date) => {
        allDates.forEach(elem => elem.setDate(elem.getDate() + date));
    }

    let items = [];
    let currentDate = new Date();
    const constDate = new Date();

    for (let number = 0; number <= 25; ++number) {
        items.push(
            <Pagination.Item key={number} active={currentDate.getDate() === constDate.getDate()}>
            {currentDate.getDate()}
            </Pagination.Item>);
        currentDate.setDate(currentDate.getDate() + 1);
    }

    useEffect(()=> {
        console.log(allDates);
    }, [allDates])

    return (
        <>        
        <Container className="xl my-3"><h1>Доступные услуги:</h1></Container>
        <Container><Pagination size="bg" >{items}</Pagination></Container>
        <Container className="mt-4">
            {datesArray.map(
                (elem, index) => {
                return <Row key = {index}>
                        {elem.map((col, index) => <DateTimeElem func = {props.func} key = {index} date = {col}/>)}
                    </Row>
                }
                )
            }
        </Container>
        </>
    )
}

export default PickDateTime;
