import React from "react";
import axios from 'axios';
import { Button, FormGroup, Form } from "react-bootstrap";
import "./Login.css"
import apiClient from "./apiClient";

class Login extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            user: "",
            password: "",
            greeting: "",
        }
        this.handleClick = this.handleClick.bind(this);
    }

    async handleClick() {
      try {
        const response = await apiClient.post("/api/v1/auth/authenticate", {
          'email': this.state.user,
          'password': this.state.password
        })
        localStorage.setItem("token", response.data.token);
        this.state.greeting = `Добро пожаловать, ${this.state.user}`;
        window.location.href("/api/v1/demo-controller");
      }
      catch (err) {
        console.log(err);
        this.state.greeting = `Ошибка: ${err.message}`;
      }
    }

  render () {
    return (
      <div>
        <Form className = "login-form" >
          <FormGroup>
            <Form.Label htmlFor="emailInput">Email: </Form.Label>
            <Form.Control type="email" id="emailInput" placeholder="Ivanov@mail.rur" onChange={(event) => this.setState({user: event.target.value})}></Form.Control>
          </FormGroup>
          <FormGroup >
            <Form.Label htmlFor="passwordInput">Password: </Form.Label>
            <Form.Control type="password" id="passwordInput" placeholder="От 8 цифр" onChange={(event) => this.setState({password: event.target.value})}></Form.Control>
          </FormGroup>
          <Button className="m-4" variant ="outline-primary" onClick={this.handleClick}>Жмит!</Button>
        </Form>
        
        <div className="text-center">{this.state.greeting}</div>
      </div>
    )
  }  
}

export default Login;
